    LIS (Longest Increasing Subsequence) :

  DP            O(n2)
  Binary Search O(nlogn)
  Segment Tree  O(nlogn)


idx: [0  1  2  3  4  5  6]
val: [1  2  3  4  4  5  6] 에서 4를 찾을 경우 idx 3에 위치한 iterator를 리턴합니다.

#include &lt;cstdio&gt;
#include &lt;algorithm&gt;
#include &lt;vector&gt;
using namespace std;

typedef pair&lt;int, int&gt; pii;
typedef vector&lt;int&gt; vi;
typedef vector&lt;pii&gt; vii;

int n;

int main()
{
  vi inp;

  scanf("%d", &amp;n);
  for (int i = 0; i &lt; n; i++) {
    int tmp;
    scanf("%d", &amp;tmp);
    inp.push_back(tmp);
  }

  // 첫 대소 비교를 위해 -1 원소 하나를 가지고 시작
  vi ans (1, -1);
  for (int num : inp) {
    if (ans.back() &lt; num) {
      ans.push_back(num);
    } else {
      auto it = lower_bound(ans.begin(), ans.end(), num);
      *it = num;
    }
  }

  printf("%lu\n", ans.size()-1);
}


#include &lt;cstdio&gt;
#include &lt;algorithm&gt;
#include &lt;vector&gt;
#include &lt;utility&gt;
#include &lt;limits&gt;
using namespace std;

typedef pair&lt;int, int&gt; pii;
typedef vector&lt;int&gt; vi;
typedef vector&lt;pii&gt; vii;
int n;

bool comp(const pii &amp;a, const pii &amp;b);
vi lis(const vii &amp;inp);

int main()
{
  vii inp;

  scanf("%d", &amp;n);
  for (int i = 0; i &lt; n; i++) {
    int tmp;
    scanf("%d", &amp;tmp);
    inp.push_back({tmp, i});
  }

  vi ans = lis(inp);

  printf("%lu\n", ans.size());
  for (int i : ans)
    printf("%d ", i);
  printf("\n");
}

bool comp(const pii &amp;a, const pii &amp;b)
{
  return a.first &lt; b.first;
}

vi lis(const vii &amp;inp)
{
  vii ans (1, {-1, -1});
  vi backtrace (n+1, -1);
  vi lis;

  for (auto p : inp) {
    if (ans.back().first &lt; p.first) {
      /* 추가 코드 */
      backtrace[p.second] = ans.back().second;
      /* 추가 코드 END */
      ans.push_back(p);
    } else {
      auto it = lower_bound(ans.begin(), ans.end(), p, comp);
      /* 추가 코드 */
      backtrace[p.second] = (it-1)-&gt;second;
      /* 추가 코드 END */
      *it = p;
    }
  }

  /* 추가 코드 */
  for (int cur = ans.back().second; cur &gt;= 0; cur = backtrace[cur])
    lis.push_back(inp[cur].first);
  reverse(lis.begin(), lis.end());
  /* 추가 코드 END */
  return lis;
}


